const easyHighScoresList=document.getElementById("easyHighScores"),mediumHighScoresList=document.getElementById("mediumHighScores"),hardHighScoresList=document.getElementById("hardHighScores"),easyHighScores=JSON.parse(localStorage.getItem("easyHighScores"))||[],mediumHighScores=JSON.parse(localStorage.getItem("mediumHighScores"))||[],hardHighScores=JSON.parse(localStorage.getItem("hardHighScores"))||[];easyHighScoresList.innerHTML=easyHighScores.map(e=>`<li class="highscores__item">${e.name}-${e.score}</li>`).join(),mediumHighScoresList.innerHTML=mediumHighScores.map(e=>`<li class="highscores__item highscores__item--yellow">${e.name}-${e.score}</li>`).join(),hardHighScoresList.innerHTML=hardHighScores.map(e=>`<li class="highscores__item highscores__item--red">${e.name}-${e.score}</li>`).join();